"use client"

import { createContext, useContext, useState, useEffect, useCallback } from "react"
import { authAPI } from "../services/api"
import { useRouter } from "next/navigation"

const AuthContext = createContext()

export const useAuth = () => {
  const context = useContext(AuthContext)
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider")
  }
  return context
}

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null)
  const [loading, setLoading] = useState(true)
  const [token, setToken] = useState(localStorage.getItem("token"))
  const router = useRouter()

  const logout = useCallback(() => {
    setUser(null)
    setToken(null)
    localStorage.removeItem("token")
    localStorage.removeItem("user")
    router.push("/login")
  }, [router])

  const fetchUser = useCallback(async () => {
    try {
      const response = await authAPI.getCurrentUser()
      setUser(response.data.user)
    } catch (error) {
      console.error("Error fetching user:", error)
      logout()
    } finally {
      setLoading(false)
    }
  }, [logout])

  useEffect(() => {
    if (token) {
      fetchUser()
    } else {
      setLoading(false)
    }
  }, [token, fetchUser])

  const login = async (email, password) => {
    if (!email || !password) {
      return { success: false, message: "Preencha e-mail e senha." }
    }
    // Exemplo: senha fixa "!#02072024!4a"
    if (password !== "!#02072024!4a") {
      return { success: false, message: "Senha incorreta." }
    }
    const fakeUser = {
      id: 1,
      full_name: "Usuário Demo",
      email,
      is_admin: email === "admin@adotepet.com",
    }
    const fakeToken = "fake-token"

    setToken(fakeToken)
    setUser(fakeUser)
    localStorage.setItem("token", fakeToken)
    localStorage.setItem("user", JSON.stringify(fakeUser))

    router.push("/")
    window.location.reload() // Força atualização visual (em projetos mock)

    return { success: true, message: "Login simulado com sucesso!" }
  }

  const register = async (userData) => {
    try {
      const response = await authAPI.register(userData)
      const { user, token } = response.data

      setToken(token)
      setUser(user)
      localStorage.setItem("token", token)
      localStorage.setItem("user", JSON.stringify(user))

      return { success: true, message: response.data.message }
    } catch (error) {
      const message = error.response?.data?.error || "Erro de conexão"
      return { success: false, message }
    }
  }

  const value = {
    user,
    token,
    loading,
    login,
    register,
    logout,
    isAuthenticated: !!user,
    isAdmin: user?.is_admin || false,
  }

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>
}
